SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
START TRANSACTION;
SET time_zone = "+00:00";

DROP PROCEDURE IF EXISTS CONSUMER_PATH;
DROP FUNCTION IF EXISTS GET_CHILD_CONSUMERS;

DELIMITER $$
--
-- Procedures
--
CREATE PROCEDURE `CONSUMER_PATH` (IN `input` INT, OUT `output` VARCHAR(128))   Begin
DECLARE _id INT;
  DECLARE _parent INT;
  DECLARE _path VARCHAR(128);

  SET `max_sp_recursion_depth` = 50;

  SELECT `id`, `parent_location_id`
  INTO _id, _parent
  FROM `consumers`
  WHERE `consumers`.`id` = `input` and `consumers`.`deleted`=0 and `consumers`.`record_status`=1;

  IF _parent IS NULL THEN
    SET _path = _id;
  ELSE
    CALL `CONSUMER_PATH`(_parent, _path);
    SELECT CONCAT(_path, ',', _id) INTO _path;
  END IF;

  SELECT _path INTO `output`;

END$$

--
-- Functions
--
CREATE FUNCTION `GET_CHILD_CONSUMERS` (`input` INT) RETURNS VARCHAR(128) CHARSET utf8mb4   BEGIN
  CALL `CONSUMER_PATH`(`input`, @path);
  RETURN @path;
END$$

DELIMITER ;

