CREATE TRIGGER `add_consumer_wise_user_access` AFTER INSERT ON `consumers` FOR EACH ROW BEGIN
DECLARE parent_consumers TEXT;
DECLARE userid INT;

-- Get the parent consumers
CALL CONSUMER_PATH(new.id, @parent_consumers);

-- Insert all location access users 
INSERT INTO consumer_wise_user_access (subscriber_id, consumer_id, consumer_name, parent_consumer_id, user_id)
SELECT DISTINCT new.subscriber_id, new.id, new.name, new.parent_location_id, user_id
FROM domain_users
WHERE all_office_locations = 1
  AND deleted = 0
  AND record_status = 1
  AND (new.id, user_id, 0) NOT IN (
    SELECT consumer_id, user_id, deleted
    FROM consumer_wise_user_access
  );

-- Insert parent location access users
INSERT INTO consumer_wise_user_access (subscriber_id, consumer_id, consumer_name, parent_consumer_id, user_id)
SELECT DISTINCT new.subscriber_id, new.id, new.name, new.parent_location_id, domain_users.user_id
FROM consumer_users
LEFT JOIN domain_users ON domain_users.id = consumer_users.domain_user_id
WHERE consumer_users.all_children = 'Yes'
  AND consumer_users.consumer_id IN (@parent_consumers)
  AND (new.id, domain_users.user_id, 0) NOT IN (
    SELECT consumer_id, user_id, deleted
    FROM consumer_wise_user_access
  );

END
$$
