  IF NEW.name <> OLD.name THEN
   update consumer_wise_user_access set consumer_name=NEW.name,parent_consumer_id=NEW.parent_location_id where consumer_name=OLD.name;
  END IF;
  
  -- Update the deleted status of the consumer_wise_user_access record
  UPDATE consumer_wise_user_access SET deleted = 1 WHERE consumer_id = NEW.id;
  
  -- Check if parent_location_id has been changed
  IF NEW.parent_location_id IS NULL THEN
    -- Insert users with all office locations access or update the existing record if it exists
    INSERT INTO consumer_wise_user_access (subscriber_id, consumer_id, consumer_name, parent_consumer_id, user_id, deleted)
    SELECT DISTINCT NEW.subscriber_id, NEW.id, NEW.name, NEW.parent_location_id, user_id, 0
    FROM domain_users
    WHERE all_office_locations = 1
      AND deleted = 0
      AND record_status = 1
    ON DUPLICATE KEY UPDATE deleted = 0;
  ELSE
    CALL CONSUMER_PATH(NEW.id, @parent_consumers);
  
    -- Insert parent location access users or update the existing record if it exists
    INSERT INTO consumer_wise_user_access (subscriber_id, consumer_id, consumer_name, parent_consumer_id, user_id, deleted)
